import React from "react";
import Item from "./Item";

export default class extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            data: {
                items: null,
                pagination: null
            }
        };
    }

    async componentDidMount() {
        await fetch("https://api.jqestate.ru/v1/properties/country")
            .then(response => response.json())
            .then(data => {
                console.log(data);
                if (!data) {
                    this.setState({
                        errorMesaage: "No objects"
                    });
                } else {
                    this.setState({
                        data: {
                            items: data.items,
                            pagination: data.pagination
                        }
                    });
                }
            });
    }

    render() {
        const {
            data: { items, pagination },
            errorMesaage
        } = this.state;
        if (errorMesaage) return <div>{errorMesaage}</div>;
        return items ? <div>I got it</div> : <div>LOADING...</div>;
    }
}
